import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';
import { AiService } from './ai.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'what-you-say';

  constructor(public aiService: AiService) {}

  textToAnalyse = new FormControl()

  callApi() {
    this.aiService.analyse(this.textToAnalyse)
    .then(response => {
      console.log(response)
    }).catch(error)
  }
}
<label for="analysingText">Text you want to analyse: </label><br/>
<input id="analysingText" type="text" [formControl]="textToAnalyse"><br/><br/>

<button (click)="callApi()">Analyse</button>
