import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'what-you-say';
}
<label for="analysingText">Text you want to analyse: </label><br/>
<input id="analysingText" type="text" [formControl]="analysingTest"><br/><br/>

<button (click)="callApi()">Analyse</button>
